import React from 'react';




class AccountManage{
    constructor(){
        var logginStatus=true;
    }
    currentLog = () =>{
        return this.logginStatus;
    }

    doLogIn = () =>{
        logginStatus=true;
    }

    doLogOut = () =>{
        logginStatus=false;
    }

}

const AccountManageContext = React.createContext(
    {
        currentLog:()=>{},
        doLogIn:()=>{},
        doLogOut:()=>{}
    }
);

export default AccountManageContext;
export {AccountManage}
