import React,{useContext} from 'react';


var logginStatus=true;


const currentLog = () =>{
    return logginStatus;
}

const doLogIn = () =>{
    logginStatus=true;
}

const doLogOut = () =>{
    logginStatus=false;
}

const AccountManage = React.createContext(
    {
        currentLog:currentLog,
        doLogIn:doLogIn,
        doLogOut:doLogOut
    }
);

export default AccountManage;
