import React,{useContext} from 'react';
import AccountManage from '../../Contexts/AccountManage/AccountManage';
import {Link} from 'react-router-dom';
import './Header.css';
import PATH from '../../Path';
import logo from '../../assets/Logotruonglqd.jpg';
import Navbar from '../Navbar/Navbar';






const Header = (props) =>{
    const accountManage=useContext(AccountManage);
    let ifLogged=accountManage.currentLog();
    const toLogin = (
        <div className="header-component_login-bar">
            <Link onClick={accountManage.doLogIn} to={PATH.LOGIN}>
                Login
            </Link>
            <Link to={PATH.SIGNUP}>
                Register
            </Link>
        </div>
    );


    const loggedIn = (
        <div className="header-component_login-bar">
            <Link onClick={accountManage.doLogOut} to={PATH.LOGIN}>
                Log Out
            </Link>

        </div>
    );
    return (
        <div id="Header">
            {(ifLogged===false)?toLogin:loggedIn}
            <div className="logo-div">
                <a href="/">
                    <img src={logo} className="top-logo" alt="le quy don logo"/>
                </a>
            </div>
            <Navbar listOfSections={props.listOfSections}/>
        </div>
    )
}
export default Header;
