import React from 'react';


var logginStatus=true;


const currentLog = () =>{
    return logginStatus;
}

const doLogIn = () =>{
    logginStatus=true;
}

const doLogOut = () =>{
    logginStatus=false;
}

const AccountManage = React.createContext(
    {
        currentLog:()=>{},
        doLogIn:()=>{},
        doLogOut:()=>{}
    }
);

export default AccountManage;
